//New 'TranslateBoneToPhysBone' since the other one doesn't really work very welllocal meta = FindMetaTable("Entity")function meta:TranslateBoneToPhysBoneNew(bonenum)	if self:GetPhysicsObjectCount() < 1 then return -1 end	for i=0,self:GetPhysicsObjectCount()-1 do		local b = self:TranslatePhysBoneToBone(i)		local bp = self:GetBoneParent(b)		if b == bonenum then			return i		end	end	return -1end